<style>
body{
	background: #fff;
}
.ull{

}
.ull li {
	color: #444;
	margin: 20px;
}
#anim_dog_element{
    position: fixed;
    bottom: 0;
    z-index: 9999;
    width: 200px;
    left: 88%;
    animation: 20s dog-run-bottom infinite;
}

#anim_dog_element img{
    max-width: 100%;
}

@keyframes dog-run-bottom{
	0%{
		left: -10%;
		transform: rotateY(180deg);
	}
	49%{
		transform: rotateY(180deg);
	}
	50%{
		left: 100%;
		transform: rotateY(0);
	}
	100%{
		left: -10%;
		transform: rotateY(0);
	}
}
</style>
<script>
(function() {
var anim_dog_element = document.createElement('div')
anim_dog_element.id = 'anim_dog_element'
anim_dog_element.innerHTML = `
